import React, { useState, useEffect } from "react";

export default function ProductManagement() {
  const [name, setNombre] = useState("");
  const [description, setDescripcion] = useState("");
  const [salePrice, setPrecio] = useState(0);
  const [size, setTalla] = useState("");
  const [type, setTipo] = useState("");
  const [color, setColor] = useState("");
  const [material, setMaterial] = useState("");
  const [gender, setGenero] = useState("");
  const [brand, setMarca] = useState("");
  const [quantity, setCantidad] = useState(0);
  const [costPrice, setPrecioCosto] = useState(0);
  const [images, setImage] = useState(null); // State for the image file
  const [editar, setEditar] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResult, setSearchResult] = useState("");
  const [editingProduct, setEditingProduct] = useState(null);

  const handleFileChange = (event) => {
    setImage(event.target.files[0]);
  };

  const handleSubmit = async () => {
    const formData = new FormData();
    formData.append("name", name);
    formData.append("description", description);
    formData.append("type", type);
    formData.append("material", material);
    formData.append("size", size);
    formData.append("gender", gender);
    formData.append("color", color);
    formData.append("brand", brand);
    formData.append("quantity", quantity);
    formData.append("costPrice", costPrice);
    formData.append("salePrice", salePrice);
    if (images) {
      formData.append("photos", images); // Append the image file to form data
    }

    try {
      const token = localStorage.getItem('token'); 

      const response = await fetch("http://localhost:3001/api/products/", {
        method: "POST",
        headers: {
          "Authorization": `Bearer ${token}`, 
        },
        body: formData, 
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to add product");
      }

      const result = await response.json();
      console.log("Product added successfully:", result);
      alert("Product added successfully!");
      fetchProducts(); // Refresh product list after adding
    } catch (error) {
      console.error("Error:", error);
      alert("Error adding product!");
    }
  };

  const [products, setProducts] = useState([]);

  const fetchProducts = async () => {
    try {
      const token = localStorage.getItem('token'); 

      const response = await fetch("http://localhost:3001/api/products/", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`, 
        },
      });

      if (!response.ok) {
        throw new Error("Failed to fetch products");
      }

      const productsData = await response.json();
      setProducts(productsData);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem('token'); 

      const response = await fetch(`http://localhost:3001/api/products/${id}`, {
        method: "DELETE",
        headers: {
          "Authorization": `Bearer ${token}`, 
        },
      });

      if (!response.ok) {
        throw new Error("Failed to delete product");
      }

      console.log("Product deleted successfully");
      alert("Product deleted successfully!");
      fetchProducts();
    } catch (error) {
      console.error("Error:", error);
      alert("Error deleting product!");
    }
  };

  const handleSearch = async () => {
    try {
      const token = localStorage.getItem('token'); 

      const response = await fetch(`http://localhost:3001/api/products/search/${searchQuery}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`, 
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to search product");
      }

      const result = await response.json();
      setSearchResult(result[0]);
    } catch (error) {
      console.error("Error:", error);
      alert("Error searching product!");
    }
  };

  const handleUpdate = async () => {
    if (!editingProduct) {
      alert("No product is being edited!");
      return;
    }

    const formData = new FormData();
    formData.append("name", name);
    formData.append("description", description);
    formData.append("type", type);
    formData.append("material", material);
    formData.append("size", size);
    formData.append("gender", gender);
    formData.append("color", color);
    formData.append("brand", brand);
    formData.append("quantity", quantity);
    formData.append("costPrice", costPrice);
    formData.append("salePrice", salePrice);

    try {
      const token = localStorage.getItem("token");

      const response = await fetch(`http://localhost:3001/api/products/${editingProduct._id}`, {
        method: "PUT",
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to update product");
      }

      const result = await response.json();
      console.log("Product updated successfully:", result);
      alert("Product updated successfully!");

      // Refresh product list
      setEditingProduct(null);
      const updatedProducts = products.map((product) =>
        product._id === editingProduct._id ? result : product
      );
      setProducts(updatedProducts);
    } catch (error) {
      console.error("Error:", error);
      alert("Error updating product!");
    }
  };

  const openEditPopup = (product) => {
    setEditingProduct(product);
    setNombre(product.name);
    setDescripcion(product.description);
    setPrecio(product.salePrice);
    setTalla(product.size);
    setTipo(product.type);
    setColor(product.color);
    setMaterial(product.material);
    setGenero(product.gender);
    setMarca(product.brand);
    setCantidad(product.quantity);
    setPrecioCosto(product.costPrice);
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <div className='container'>
      <div className="App">
        <div className="datos-producto">
        </div>
      </div>
      <div className="card text-center">
        <div className="card-header">
          Agregar Producto
        </div>
        <div className="card-body">
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Nombre</span>
            <input type="text"
              value={name}
              onChange={(event) => setNombre(event.target.value)}
              className="form-control" placeholder="Ingrese nombre del producto" aria-label="Nombre" aria-describedby="basic-addon1" />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Descripción</span>
            <input type="text"
              value={description}
              onChange={(event) => setDescripcion(event.target.value)}
              className="form-control" placeholder="Ingrese descripción del producto" aria-label="Descripción" aria-describedby="basic-addon1" />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Precio</span>
            <input type="number"
              value={salePrice}
              onChange={(event) => setPrecio(event.target.value)}
              className="form-control" placeholder="Ingrese precio del producto" aria-label="Precio" aria-describedby="basic-addon1" />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Talla</span>
            <input type="text"
              value={size}
              onChange={(event) => setTalla(event.target.value)}
              className="form-control" placeholder="Ingrese talla del producto" aria-label="Talla" aria-describedby="basic-addon1" />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Tipo</span>
            <input type="text"
              value={type}
              onChange={(event) => setTipo(event.target.value)}
              className="form-control" placeholder="Ingrese tipo del producto" aria-label="Tipo" aria-describedby="basic-addon1" />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Color</span>
            <input type="text"
              value={color}
              onChange={(event) => setColor(event.target.value)}
              className="form-control" placeholder="Ingrese color del producto" aria-label="Color" aria-describedby="basic-addon1" />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Material</span>
            <input type="text"
              value={material}
              onChange={(event) => setMaterial(event.target.value)}
              className="form-control" placeholder="Ingrese material del producto" aria-label="Material" aria-describedby="basic-addon1" />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Género</span>
            <input type="text"
              value={gender}
              onChange={(event) => setGenero(event.target.value)}
              className="form-control" placeholder="Ingrese género del producto" aria-label="Género" aria-describedby="basic-addon1" />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Marca</span>
            <input type="text"
              value={brand}
              onChange={(event) => setMarca(event.target.value)}
              className="form-control" placeholder="Ingrese marca del producto" aria-label="Marca" aria-describedby="basic-addon1" />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Cantidad</span>
            <input type="number"
              value={quantity}
              onChange={(event) => setCantidad(event.target.value)}
              className="form-control" placeholder="Ingrese cantidad del producto" aria-label="Cantidad" aria-describedby="basic-addon1" />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Precio Costo</span>
            <input type="number"
              value={costPrice}
              onChange={(event) => setPrecioCosto(event.target.value)}
              className="form-control" placeholder="Ingrese precio costo del producto" aria-label="Precio Costo" aria-describedby="basic-addon1" />
          </div>
          <div className="input-group mb-3">
            <input type="file" onChange={handleFileChange} className="form-control" id="inputGroupFile02" />
            <label className="input-group-text" htmlFor="inputGroupFile02">Subir</label>
          </div>
          <div className="d-grid gap-2">
            <button onClick={handleSubmit} className="btn btn-primary" type="button">Agregar</button>
          </div>
        </div>
        <div className="card-footer text-body-secondary">
          <div className="input-group mb-3">
            <input type="text"
              value={searchQuery}
              onChange={(event) => setSearchQuery(event.target.value)}
              className="form-control" placeholder="Buscar producto" aria-label="Buscar" aria-describedby="basic-addon1" />
            <button onClick={handleSearch} className="btn btn-outline-secondary" type="button">Buscar</button>
          </div>
          {searchResult && (
            <div>
              <h5>Resultados de búsqueda:</h5>
              <p>Nombre: {searchResult.name}</p>
              <p>Descripción: {searchResult.description}</p>
              <p>Precio: {searchResult.salePrice}</p>
              <p>Talla: {searchResult.size}</p>
              <p>Tipo: {searchResult.type}</p>
              <p>Color: {searchResult.color}</p>
              <p>Material: {searchResult.material}</p>
              <p>Género: {searchResult.gender}</p>
              <p>Marca: {searchResult.brand}</p>
              <p>Cantidad: {searchResult.quantity}</p>
              <p>Precio Costo: {searchResult.costPrice}</p>
              <button onClick={() => openEditPopup(searchResult)} className="btn btn-outline-primary" type="button">Editar</button>
              <button onClick={() => handleDelete(searchResult._id)} className="btn btn-outline-danger" type="button">Eliminar</button>
            </div>
          )}
        </div>
      </div>
      <div className="mt-4">
        <h3>Lista de Productos</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Descripción</th>
              <th>Precio</th>
              <th>Talla</th>
              <th>Tipo</th>
              <th>Color</th>
              <th>Material</th>
              <th>Género</th>
              <th>Marca</th>
              <th>Cantidad</th>
              <th>Precio Costo</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => (
              <tr key={product._id}>
                <td>{product.name}</td>
                <td>{product.description}</td>
                <td>{product.salePrice}</td>
                <td>{product.size}</td>
                <td>{product.type}</td>
                <td>{product.color}</td>
                <td>{product.material}</td>
                <td>{product.gender}</td>
                <td>{product.brand}</td>
                <td>{product.quantity}</td>
                <td>{product.costPrice}</td>
                <td>
                  <button onClick={() => openEditPopup(product)} className="btn btn-outline-primary" type="button">Editar</button>
                  <button onClick={() => handleDelete(product._id)} className="btn btn-outline-danger" type="button">Eliminar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
