import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const ProductDetail = () => {
    <h1>kafje</h1>
  const { productId } = useParams();
  console.log(productId);
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        console.log("Fetching product details...");
        const token = localStorage.getItem('token'); // Get the token from localStorage

        const response = await fetch(`http://localhost:3001/api/products/${productId}`, {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`, // Include the token in the headers
          },
        });

        if (!response.ok) {
          throw new Error("Failed to fetch product details");
        }
        const productData = await response.json();
        console.log("Fetched product details:", productData);
        setProduct(productData);
      } catch (error) {
        console.error("Error:", error);
      }
    };

    fetchProduct();
  }, [productId]);

  if (!product) {
    console.log("Product is null. Loading...");
    return <div>Loading...</div>;
  }

  console.log("Product is loaded:", product);
  
  return (
    <div className="container">
      <h1>{product.name}</h1>
      <p>Description: {product.description}</p>
      <p>Type: {product.type}</p>
      <p>Material: {product.material}</p>
      <p>Size: {product.size}</p>
      <p>Gender: {product.gender}</p>
      <p>Color: {product.color}</p>
      <p>Brand: {product.brand}</p>
      <p>Quantity: {product.quantity}</p>
      <p>Cost Price: {product.costPrice}</p>
      <p>Sale Price: {product.salePrice}</p>
      {product.images.length > 0 && (
        <img 
          src={`http://localhost:3001/${product.images[0].path}`} 
          alt={product.images[0].originalname} 
          style={{ width: "200px", height: "auto" }} 
        />
      )}
    </div>
  );
};

export default ProductDetail;
