import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [name, setName] = useState('');
  const [email, setSignupEmail] = useState('');
  const [password, setSignupPassword] = useState('');
  const [age, setAge] = useState('');
  const [gender, setGender] = useState('');
  const [users, setUsers] = useState([]);
  const [editingUser, setEditingUser] = useState(null);

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleSignup = async () => {
    const token = localStorage.getItem('token'); // Get the token from localStorage

    const user = {
      name,
      email,
      password,
      age: parseInt(age, 10), // Ensure age is sent as a number
      gender,
    };

    try {
      const response = await axios.post('http://localhost:3001/api/users/', user, {
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
      });
      console.log('User registered successfully:', response.data);
      // Optionally, reset the form fields
      setName('');
      setSignupEmail('');
      setSignupPassword('');
      setAge('');
      setGender('');

      // Fetch the updated list of users
      fetchUsers();
    } catch (error) {
      console.error('Error registering user:', error);
    }
  };

  const fetchUsers = async () => {
    const token = localStorage.getItem('jwt'); // Get the token from localStorage

    try {
      const response = await axios.get('http://localhost:3001/api/users/', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const openEditPopup = (user) => {
    setEditingUser(user);
    setName(user.name);
    setSignupEmail(user.email);
    setSignupPassword(''); // Do not pre-fill password for security reasons
    setAge(user.age);
    setGender(user.gender);
  };

  const handleUpdate = async () => {
    const token = localStorage.getItem('jwt'); // Get the token from localStorage

    const updatedUser = {
      name,
      email,
      password,
      age: parseInt(age, 10), // Ensure age is sent as a number
      gender,
    };

    try {
      const response = await axios.put(`http://localhost:3001/api/users/${editingUser._id}`, updatedUser, {
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
      });
      console.log('User updated successfully:', response.data);
      setEditingUser(null);
      fetchUsers();
    } catch (error) {
      console.error('Error updating user:', error);
    }
  };

  const handleDelete = async (userId) => {
    const token = localStorage.getItem('jwt'); // Get the token from localStorage

    try {
      await axios.delete(`http://localhost:3001/api/users/${userId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      console.log('User deleted successfully');
      fetchUsers();
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  return (
    <div className="container">
      <div className="App">
        <div className="datos-producto"></div>
      </div>

      <div className="card text-center">
        <div className="card-header">Registrarse</div>
        <div className="card-body">
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Nombre</span>
            <input
              type="text"
              value={name}
              onChange={(event) => setName(event.target.value)}
              className="form-control"
              placeholder="Ingrese su nombre"
              aria-label="Name"
              aria-describedby="basic-addon1"
            />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Email</span>
            <input
              type="email"
              value={email}
              onChange={(event) => setSignupEmail(event.target.value)}
              className="form-control"
              placeholder="Ingrese su email"
              aria-label="Email"
              aria-describedby="basic-addon1"
            />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Contraseña</span>
            <input
              type="password"
              value={password}
              onChange={(event) => setSignupPassword(event.target.value)}
              className="form-control"
              placeholder="Ingrese su contraseña"
              aria-label="Password"
              aria-describedby="basic-addon1"
            />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Edad</span>
            <input
              type="number"
              value={age}
              onChange={(event) => setAge(event.target.value)}
              className="form-control"
              placeholder="Ingrese su edad"
              aria-label="Age"
              aria-describedby="basic-addon1"
            />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Género</span>
            <input
              type="text"
              value={gender}
              onChange={(event) => setGender(event.target.value)}
              className="form-control"
              placeholder="Ingrese su género"
              aria-label="Gender"
              aria-describedby="basic-addon1"
            />
          </div>
        </div>
        <div className="card-footer text-muted">
          <button className="btn btn-success" onClick={handleSignup}>
            Agregar
          </button>
        </div>
      </div>

      <div className="card text-center mt-4">
        <div className="card-header">Lista de Usuarios</div>
        <div className="card-body">
          {users.length > 0 ? (
            <ul className="list-group">
              {users.map((user) => (
                <li key={user._id} className="list-group-item">
                  {user.name} - {user.email} - {user.age} años - {user.gender}
                  <div className="mt-2">
                    <button
                      type="button"
                      onClick={() => openEditPopup(user)}
                      className="btn btn-info me-2"
                    >
                      Editar
                    </button>
                    <button
                      type="button"
                      className="btn btn-danger"
                      onClick={() => handleDelete(user._id)}
                    >
                      Eliminar
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          ) : (
            <p>No users found.</p>
          )}
        </div>
      </div>

      {editingUser && (
        <div className="card text-center mt-4">
          <div className="card-header">Editar Usuario</div>
          <div className="card-body">
            <div className="input-group mb-3">
              <span className="input-group-text" id="basic-addon1">Nombre</span>
              <input
                type="text"
                value={name}
                onChange={(event) => setName(event.target.value)}
                className="form-control"
                placeholder="Ingrese su nombre"
                aria-label="Name"
                aria-describedby="basic-addon1"
              />
            </div>
            <div className="input-group mb-3">
              <span className="input-group-text" id="basic-addon1">Email</span>
              <input
                type="email"
                value={email}
                onChange={(event) => setSignupEmail(event.target.value)}
                className="form-control"
                placeholder="Ingrese su email"
                aria-label="Email"
                aria-describedby="basic-addon1"
              />
            </div>
            <div className="input-group mb-3">
              <span className="input-group-text" id="basic-addon1">Contraseña</span>
              <input
                type="password"
                value={password}
                onChange={(event) => setSignupPassword(event.target.value)}
                className="form-control"
                placeholder="Ingrese su contraseña"
                aria-label="Password"
                aria-describedby="basic-addon1"
              />
            </div>
            <div className="input-group mb-3">
              <span className="input-group-text" id="basic-addon1">Edad</span>
              <input
                type="number"
                value={age}
                onChange={(event) => setAge(event.target.value)}
                className="form-control"
                placeholder="Ingrese su edad"
                aria-label="Age"
                aria-describedby="basic-addon1"
              />
            </div>
            <div className="input-group mb-3">
              <span className="input-group-text" id="basic-addon1">Género</span>
              <input
                type="text"
                value={gender}
                onChange={(event) => setGender(event.target.value)}
                className="form-control"
                placeholder="Ingrese su género"
                aria-label="Gender"
                aria-describedby="basic-addon1"
              />
            </div>
          </div>
          <div className="card-footer text-muted">
            <div className="button-group">
              <button className="btn btn-warning" onClick={handleUpdate}>
                Actualizar
              </button>
              <button className="btn btn-secondary" onClick={() => setEditingUser(null)}>
                Cancelar
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
