import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css';

export default function Home() {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const token = localStorage.getItem('token'); 

        const response = await fetch("http://localhost:3001/api/products/", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`, 
          },
        });

        if (!response.ok) {
          throw new Error("Failed to fetch products");
        }

        const productsData = await response.json();
        setProducts(productsData);
      } catch (error) {
        console.error("Error:", error);
      }
    };

    fetchProducts(); 
  }, []);

  const handleViewProduct = (productId) => {
    navigate(`/products/${productId}`);
  };


  return (
    <div>
      <h1>Products</h1>
      
      <div className="d-flex justify-content-center align-items-center min-vh-100">
      <div className="container">
        <div className="row">
          {products.map(product => (
            <div key={product._id} className="col-md-3 mb-4">
              <div className="card">
                {product.images.length > 0 && (
                  <img 
                    src={`http://localhost:3001/${product.images[0].path}`} 
                    alt={product.images[0].originalname} 
                    className="card-img-top" 
                  />
                )}
                <div className="card-body">
                  <h5 className="card-title">{product.name}</h5>
                  <p className="card-text">precio: {product.salePrice}</p>
                  <a href="#" className="btn btn-primary" onClick={() => handleViewProduct(product._id)}>ver producto</a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
    </div>
  );
}
