import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css';

export default function Home() {
  const [products, setProducts] = useState([]);
  const [filterOptions, setFilterOptions] = useState({
    type: [],
    material: [],
    size: [],
    gender: [],
    color: [],
    brand: [],
  });
  const [selectedFilters, setSelectedFilters] = useState({
    type: [],
    material: [],
    size: [],
    gender: [],
    color: [],
    brand: [],
  });
  const [filteredProducts, setFilteredProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const token = localStorage.getItem('token'); 

        const response = await fetch("http://localhost:3001/api/products/", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`, 
          },
        });

        if (!response.ok) {
          throw new Error("Failed to fetch products");
        }

        const productsData = await response.json();
        setProducts(productsData);
      } catch (error) {
        console.error("Error:", error);
      }
    };

    fetchProducts(); 
  }, []);

  const getUniqueOptions = (data, attribute) => {
    const options = new Set();
    data.forEach((product) => {
      options.add(product[attribute]);
    });
    return [...options];
  };

  const handleFilterChange = (attribute, value) => {
    setSelectedFilters({
      ...selectedFilters,
      [attribute]: value,
    });
  };

  useEffect(() => {
    applyFilters();
  }, [selectedFilters, products]);

  const applyFilters = () => {
    let filtered = [...products];

    Object.entries(selectedFilters).forEach(([attribute, values]) => {
      if (values.length > 0) {
        filtered = filtered.filter((product) =>
          values.includes(product[attribute])
        );
      }
    });

    setFilteredProducts(filtered);
  };

  return (
    <div>
    <div className="container">
      <div>
        {Object.entries(filterOptions).map(([attribute, options]) => (
          <div key={attribute}>
            <h4>{attribute}</h4>
            {options.map((option) => (
              <div key={option}>
                <input
                  type="checkbox"
                  id={`${attribute}-${option}`}
                  value={option}
                  checked={selectedFilters[attribute].includes(option)}
                  onChange={(e) =>
                    handleFilterChange(attribute, e.target.value)
                  }
                />
                <label htmlFor={`${attribute}-${option}`}>{option}</label>
              </div>
            ))}
          </div>
        ))}
      </div>
      <div>
        {filteredProducts.map((product) => (
          <div key={product.id}>
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            {/* Display other product details */}
          </div>
        ))}
      </div>
    </div>

    <div className="d-flex justify-content-center align-items-center min-vh-100">
      <div className="container">
        <h1 className="text-center">Products</h1>
        <div className="row">
          {products.map(product => (
            <div key={product._id} className="col-md-3 mb-4">
              <div className="card">
                {product.images.length > 0 && (
                  <img 
                    src={`http://localhost:3001/${product.images[0].path}`} 
                    alt={product.images[0].originalname} 
                    className="card-img-top" 
                  />
                )}
                <div className="card-body">
                  <h5 className="card-title">{product.name}</h5>
                  <p className="card-text">precio: {product.salePrice}</p>
                  <Link to={`/products/${product._id}`} className="btn btn-primary">
                    ver producto
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
    </div>
  );
}
