import React, { useState, useEffect } from "react";

export default function AdminDashboard() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");
  const [editingUser, setEditingUser] = useState(null);
  const [users, setUsers] = useState([]);

  const handleSubmit = async () => {
    const userData = { name, email, password, role };

    try {
      const token = localStorage.getItem('token');

      const response = await fetch("http://localhost:3001/api/users/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`,
        },
        body: JSON.stringify(userData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to add user");
      }

      const result = await response.json();
      console.log("User added successfully:", result);
      alert("User added successfully!");
      fetchUsers();
    } catch (error) {
      console.error("Error:", error);
      alert("Error adding user!");
    }
  };

  const fetchUsers = async () => {
    try {
      const token = localStorage.getItem('token');

      const response = await fetch("http://localhost:3001/api/users/", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error("Failed to fetch users");
      }

      const usersData = await response.json();
      setUsers(usersData);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem('token');

      const response = await fetch(`http://localhost:3001/api/users/${id}`, {
        method: "DELETE",
        headers: {
          "Authorization": `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error("Failed to delete user");
      }

      console.log("User deleted successfully");
      alert("User deleted successfully!");
      fetchUsers();
    } catch (error) {
      console.error("Error:", error);
      alert("Error deleting user!");
    }
  };


  const handleUpdate = async () => {
    const updateData = { name, email, password, role };
    const token = localStorage.getItem("token");

    try {
      const response = await fetch(`http://localhost:3001/api/users/${editingUser._id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`,
        },
        body: JSON.stringify(updateData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Failed to update user");
      }

      const result = await response.json();
      console.log("User updated successfully:", result);
      alert("User updated successfully!");

      setEditingUser(null);
      const updatedUsers = users.map((user) =>
        user._id === editingUser._id ? result : user
      );
      setUsers(updatedUsers);
    } catch (error) {
      console.error("Error:", error);
      alert("Error updating user!");
    }
  };

  const openEditPopup = (user) => {
    setEditingUser(user);
    setName(user.name);
    setEmail(user.email);
    setPassword(user.password);
    setRole(user.role);
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  return (
    <div className='container'>
      <div className="App">
        <div className="datos-user">
        </div>
      </div>
      <div className="card text-center">
        <div className="card-header">
          Agregar Usuario
        </div>
        <div className="card-body">
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Nombre</span>
            <input type="text"
              value={name}
              onChange={(event) => {
                setName(event.target.value);
              }}
              className="form-control" placeholder="Ingrese nombre del usuario" aria-label="Nombre" aria-describedby="basic-addon1" />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Email</span>
            <input type="email"
              value={email}
              onChange={(event) => {
                setEmail(event.target.value);
              }}
              className="form-control" placeholder="Ingrese email del usuario" aria-label="Email" aria-describedby="basic-addon1" />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Password</span>
            <input type="password"
              value={password}
              onChange={(event) => {
                setPassword(event.target.value);
              }}
              className="form-control" placeholder="Ingrese password del usuario" aria-label="Password" aria-describedby="basic-addon1" />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Role</span>
            <input type="text"
              value={role}
              onChange={(event) => {
                setRole(event.target.value);
              }}
              className="form-control" placeholder="Ingrese role del usuario" aria-label="Role" aria-describedby="basic-addon1" />
          </div>
        </div>
        <div className="card-footer text-muted">
          {
            editingUser ? <div>
              <button className='btn btn-warning m-2' onClick={handleUpdate}>Actualizar</button>
              <button className='btn btn-info' onClick={() => setEditingUser(null)}>Cancelar</button>
            </div>
              : <button className='btn btn-success' onClick={handleSubmit}>Agregar</button>
          }
        </div>
      </div>
    
      <table className="table table-striped">
        <thead className="thead-dark">
          <tr>
            <th scope="col">#</th>
            <th scope="col">Nombre</th>
            <th scope="col">Email</th>
            <th scope="col">Role</th>
            <th scope="col">Acciones</th>
          </tr>
          </thead>
        <tbody>
          {users.map((user, index) => (
            <tr key={user._id}>
              <th scope="row">{index + 1}</th>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>
                <div className="btn-group" role="group" aria-label="Basic example">
                  <button type="button"
                    onClick={() => openEditPopup(user)}
                    className="btn btn-info">Editar</button>
                  <button type="button" className="btn btn-danger" onClick={() => handleDelete(user._id)}>Eliminar</button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
